    private void MaisActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MaisActionPerformed
        // TODO add your handling code here:
        int numero1 = Integer.parseInt(PrimeiroNumero.getText());
        int numero2 = Integer.parseInt(SegundoNumero.getText());
        int soma = numero1 + numero2;
        Resultado.setText(Integer.toString(soma));
        
    }//GEN-LAST:event_MaisActionPerformed

    private void MenosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MenosActionPerformed
        // TODO add your handling code here:
        int numero1 = Integer.parseInt(PrimeiroNumero.getText());
        int numero2 = Integer.parseInt(SegundoNumero.getText());
        int soma = numero1 - numero2;
        Resultado.setText(Integer.toString(soma));
    }//GEN-LAST:event_MenosActionPerformed

    private void DividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DividirActionPerformed
        // TODO add your handling code here:
        int numero1 = Integer.parseInt(PrimeiroNumero.getText());
        int numero2 = Integer.parseInt(SegundoNumero.getText());
        int soma = numero1 / numero2;
        Resultado.setText(Integer.toString(soma));
    }//GEN-LAST:event_DividirActionPerformed

