    private void MultiplicadorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MultiplicadorActionPerformed
        // TODO add your handling code here:
        int numero1 = Integer.parseInt(PrimeiroNumero.getText());
        int numero2 = Integer.parseInt(SegundoNumero.getText());
        int soma = numero1 * numero2;
        Resultado.setText(Integer.toString(soma));
    }//GEN-LAST:event_MultiplicadorActionPerformed

    private void restoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_restoActionPerformed
        // TODO add your handling code here:
        int numero1 = Integer.parseInt(PrimeiroNumero.getText());
        int numero2 = Integer.parseInt(SegundoNumero.getText());
        int soma = numero1 % numero2;
        Resultado.setText(Integer.toString(soma));
    }//GEN-LAST:event_restoActionPerformed

    private void raizActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_raizActionPerformed
        // TODO add your handling code here:
        int raizq = Integer.parseInt(raizQuadrada.getText());
        double somaq = Math.sqrt(raizq);
        ResR.setText(Integer.toString((int) somaq));
        
    }//GEN-LAST:event_raizActionPerformed

